import { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import Cookies from "js-cookie";
import jwtDecode from "jwt-decode";
import { ThreeDots } from 'react-loader-spinner';
import navLogo from "../assets/dashboard_logo.png";
import homeLogo from "../assets/expence_tracker_home_page.png";
import logo from "../assets/Logo.png";
import ExpenseCharts from './ExpenseCharts';
import ExpenseForm from "./ExpenseForm";
import "../styles/pagesstyle.css";

const Dashboard = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({ amount: "", type: "", category: "" });
  const [deleteIds, setDeleteIds] = useState([]);
  const [expenses, setExpenses] = useState([]);

  const token = Cookies.get("jwt_token");
  const email = token ? jwtDecode(token).email : null;

  // Fetch user data
  const fetchUserData = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
      setUserData(res.data);
      setExpenses(res.data.expenses || []);
    } catch (err) {
      console.error("Error fetching user data:", err);
      navigate("/login");
    } finally {
      setLoading(false);
    }
  }, [email, navigate]);

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }
    fetchUserData();
  }, [token, fetchUserData, navigate]);

  if (!token) return null;

  if (loading || !userData) {
    return (
      <div className="loader-container">
        <ThreeDots
          visible={true}
          height="50"
          width="50"
          color="#0b69ff"
          ariaLabel="three-dots-loading"
        />
      </div>
    );
  }

  // Handle add expense
  const handleAddExpense = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...formData, email };
      payload.amount = parseFloat(payload.amount);
      if (isNaN(payload.amount)) {
        alert("Please enter a valid number for amount.");
        return;
      }

      const res = await axios.post("http://localhost:5000/add-expense", payload);
      alert(res.data.message);
      setFormData({ amount: "", type: "", category: "" });
      fetchUserData();
    } catch (err) {
      console.error(err.response?.data || err);
      alert(err.response?.data?.message || "Failed to add expense.");
    }
  };

  // Handle delete expense
  const handleDeleteExpense = async (e) => {
    e.preventDefault();
    const idsArray = deleteIds
      .toString()
      .split(",")
      .map((id) => id.trim())
      .filter((id) => id !== "");

    if (!idsArray.length) {
      alert("Please enter valid IDs to delete.");
      return;
    }

    try {
      const res = await axios.delete("http://localhost:5000/delete-expense", {
        data: { email, ids: idsArray },
      });
      alert(res.data.message);
      setDeleteIds([]);
      fetchUserData();
    } catch (err) {
      console.error(err.response?.data || err);
      alert(err.response?.data?.message || "Failed to delete expenses.");
    }
  };

  // Download PDF
  const handleDownloadPDF = () => {
    if (!expenses.length) {
      alert("No expenses to generate PDF.");
      return;
    }
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Expense Report", 14, 15);

    const headers = [["ID", "Type", "Category", "Amount"]];
    const data = expenses.map((exp, index) => [
      index + 1,
      exp.type,
      exp.category,
      exp.amount,
    ]);

    doc.autoTable({
      startY: 20,
      head: headers,
      body: data,
      theme: "grid",
      styles: { halign: "center" },
      headStyles: { fillColor: [0, 153, 255] },
    });

    doc.save("expenses.pdf");
  };

  const onClickLogout = () => {
    Cookies.remove("jwt_token");
    navigate("/login");
  };

  return (
    <div className="dashboard-content">
      <div className="dashboard-nav-container">
        <nav className="dashboard-navbar">
          <div className="dashboard-logo">
            <img src={navLogo} alt="Logo" className="logo" />
          </div>
          <div className="dashboard-buttons">
            <button className="dashboard-button"><a href="#sectionHome">Home</a></button>
            <button className="dashboard-button"><a href="#sectionExpense">Expenses</a></button>
            <button className="dashboard-button"><a href="#sectionData">Your Data</a></button>
            <button className="dashboard-button" onClick={onClickLogout}>Logout</button>
          </div>
        </nav>
      </div>

      <section id="sectionHome">
        <div className="home-content">
          <div className="home-container">
            <div className="home-card">
              <div className="home-card-description">
                <h1>Hi, <span className="highlights">{userData.username}</span></h1>
                <h2>Welcome to <span className="highlights">Expense Tracker</span></h2>
                <p>Manage your daily expenses efficiently and take control of your financial health.</p>
              </div>
              <div className="home-card-image">
                <img src={homeLogo} alt="Logo" className="home-image" />
              </div>
            </div>
          </div>
        </div>
      </section>

      <section id="sectionExpense">
        <div className="expense-content">
          <div className="expense-cards">
            <div className="expense-card">
              <p>Add Expense</p>
              <form onSubmit={handleAddExpense}>
                <ExpenseForm formData={formData} setFormData={setFormData} />
                <button type="submit">Add Expense</button>
              </form>
            </div>

            <div className="expense-card">
              <p>Delete Expense</p>
              <form onSubmit={handleDeleteExpense}>
                <input
                  type="text"
                  placeholder="Enter IDs e.g., 1,2,3"
                  value={deleteIds}
                  onChange={(e) => setDeleteIds(e.target.value)}
                  required
                />
                <button type="submit">Delete Expenses</button>
              </form>
            </div>
          </div>
        </div>
      </section>

      <section id="sectionData">
        <div className="data-content">
          <div className="data-top-card">
            <h3>Total Income: ₹{userData.totalIncome}</h3>
            <h3>Total Expense: ₹{userData.totalExpense}</h3>
            <button onClick={handleDownloadPDF}>Download PDF</button>
          </div>

          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Type</th>
                <th>Category</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((exp) => (
                <tr key={exp.id}>
                  <td>{exp.user_expense_id}</td>
                  <td>{exp.type}</td>
                  <td>{exp.category}</td>
                  <td>₹{exp.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      <section id="sectionChart">
        <ExpenseCharts expenses={expenses} />
      </section>

      <footer>
        <p>&copy; 2025 Expense Tracker</p>
        <img src={logo} alt="footer-logo" />
      </footer>
    </div>
  );
};

export default Dashboard;
