import { useState } from "react";

const ExpenseForm = () => {
  const [formData, setFormData] = useState({
    type: "",
    category: "",
  });

  const handleChange = (e) => {
  const { name, value } = e.target;
  if (name === "type") {
    setFormData({ type: value, category: "" });
  } else {
    setFormData((prev) => ({ ...prev, [name]: value }));
  }
};


  // ðŸ’° Income Categories
  const incomeCategories = [
    "Salary",
    "Freelance",
    "Business",
    "Investments",
    "Interest",
    "Rental Income",
    "Bonus",
    "Gift Received",
    "Refunds",
    "Other Income",
  ];

  // ðŸ§¾ Expense Categories
  const expenseCategories = [
    "Food & Dining",
    "Groceries",
    "Transportation",
    "Fuel",
    "Shopping",
    "Clothing",
    "Rent",
    "Utilities",
    "Internet & Phone",
    "Insurance",
    "Healthcare",
    "Education",
    "Entertainment",
    "Travel",
    "Subscriptions",
    "EMI / Loan",
    "Charity",
    "Gifts",
    "Taxes",
    "Maintenance",
    "Pet Care",
    "Personal Care",
    "Others",
  ];

  // ðŸ§  Choose which categories to show
  const categories =
    formData.type === "Income"
      ? incomeCategories
      : formData.type === "Expense"
      ? expenseCategories
      : [];

  return (
    <div>
      <div>
        <label htmlFor="selectType">Type (Income / Expense):</label>
      <br />
      <select
        name="type"
        required
        value={formData.type}
        onChange={handleChange}
      >
        <option value="">Select Type</option>
        <option value="Income">Income</option>
        <option value="Expense">Expense</option>
      </select>
      <br />

      </div>
      <div>
        <label htmlFor="category">Category:</label>
      <br />
      <select
        name="category"
        required
        value={formData.category}
        onChange={handleChange}
        disabled={!formData.type} // disables until user picks a type
      >
        <option value="">Select Category</option>
        {categories.map((cat, index) => (
          <option key={index} value={cat}>
            {cat}
          </option>
        ))}
      </select>
      <br />
      </div>
    </div>
  );
};

export default ExpenseForm;
