import { useState} from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "../styles/pagesstyle.css";
import navLogo from "../assets/dashboard_logo.png";
import homeLogo from "../assets/expence_tracker_home_page.png";
import logo from "../assets/Logo.png";
import Cookies from "js-cookie";

const Dashboard = ({ email }) => {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [deleteIds, setDeleteIds] = useState("");
  const [formData, setFormData] = useState({
    amount: "",
    category: "",
    description: "",
    email: email || "",
  });

  const jwtToken = Cookies.get("jwt_token");

  // âœ… Redirect if no JWT token
  if (!jwtToken) {
    return <Navigate to="/login" replace />;
  }

  const fetchUserData = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
      setUserData(res.data);
    } catch {
      setError("Failed to load data.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/add-expense", {
        ...formData,
        email,
      });
      alert(response.data.message || "Expense added successfully!");
      setFormData({ amount: "", category: "", description: "", email });
      fetchUserData();
    } catch {
      alert("Failed to add expense. Try again!");
    }
  };

  const handleDeleteExpense = async (e) => {
    e.preventDefault();
    const idsArray = deleteIds.split(",").map((id) => id.trim()).filter((id) => id !== "");
    if (!idsArray.length) {
      alert("Please enter at least one valid expense ID.");
      return;
    }
    try {
      const response = await axios.delete("http://localhost:5000/delete-expense", {
        data: { email, ids: idsArray },
      });
      alert(response.data.message);
      setDeleteIds("");
      fetchUserData();
    } catch {
      alert("Failed to delete expenses. Try again!");
    }
  };

  const handleDownloadPDF = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/get-pdf?email=${email}`);
      const expensesData = res.data;
      if (!expensesData.length) return alert("No data available to generate PDF.");

      const doc = new jsPDF();
      doc.setFontSize(16);
      doc.text("Expense Report", 14, 15);

      const headers = [["ID", "Category", "Description", "Amount", "Date", "Time"]];
      const data = expensesData.map((item, index) => [
        index + 1,
        item.category || "Unknown",
        item.description || "No description",
        item.amount,
        item.only_date || "N/A",
        item.only_time || "N/A",
      ]);

      doc.autoTable({
        startY: 20,
        head: headers,
        body: data,
        theme: "grid",
        styles: { halign: "center" },
        headStyles: { fillColor: [0, 153, 255] },
      });

      doc.save("expenses.pdf");
    } catch {
      setError("Failed to generate PDF.");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!userData) return <p>No user data found.</p>;

  return (
    <div className="dashboard-content">
      {/* Navbar */}
      <nav className="dashboard-navbar">
        <div className="dashboard-logo">
          <img src={navLogo} alt="Logo" className="logo" />
        </div>
        <div className="dashboard-buttons">
          <button className="dashboard-button"><a href="#sectionHome">Home</a></button>
          <button className="dashboard-button"><a href="#sectionExpense">Expenses</a></button>
          <button className="dashboard-button"><a href="#sectionData">Your Data</a></button>
        </div>
      </nav>

      {/* Home Section */}
      <section id="sectionHome">
        <div className="home-content">
          <div className="home-card">
            <div className="home-card-description">
              <h1>Hi, <span className="highlights">{userData.username}</span></h1>
              <h2>Welcome to <span className="highlights">Expense Tracker</span></h2>
              <h3>Track your Expenses Easily</h3>
              <p>Manage your daily expenses efficiently and take control of your financial health.</p>
              <ul>
                <li>âž• Add your daily expenses</li>
                <li>ðŸ“ˆ View spending insights</li>
                <li>ðŸ’° Set monthly budgets</li>
                <li>ðŸ”” Get expense alerts</li>
              </ul>
            </div>
            <div className="home-card-image">
              <img src={homeLogo} alt="Home Logo" />
            </div>
          </div>
        </div>
      </section>

      {/* Expense Section */}
      <section id="sectionExpense">
        <div className="expense-content">
          <div className="expense-card">
            <p>Add Expense</p>
            <form onSubmit={handleAddExpense}>
              <input type="number" name="amount" placeholder="Amount" required value={formData.amount} onChange={handleChange} />
              <select name="category" required value={formData.category} onChange={handleChange}>
                <option value="">Select Category</option>
                <option value="Income">Income</option>
                <option value="Expense">Expense</option>
              </select>
              <input type="text" name="description" placeholder="Description" value={formData.description} onChange={handleChange} />
              <button type="submit">Add Expense</button>
            </form>
          </div>

          <div className="expense-card">
            <p>Delete Expense</p>
            <form onSubmit={handleDeleteExpense}>
              <input
                type="text"
                placeholder="Enter IDs e.g., 1,2,3"
                required
                value={deleteIds}
                onChange={(e) => setDeleteIds(e.target.value)}
              />
              <button type="submit">Delete Expenses</button>
            </form>
          </div>
        </div>
      </section>

      {/* Data Section */}
      <section id="sectionData">
        <div className="data-content">
          <h3>Total Income: â‚¹{userData.totalIncome}</h3>
          <h3>Total Expense: â‚¹{userData.totalExpense}</h3>
          <h3>Total Balance: â‚¹{userData.totalBalance}</h3>
          <button onClick={handleDownloadPDF}>Download PDF</button>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Category</th>
                <th>Description</th>
                <th>Amount</th>
                <th>Date</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {userData.expenses.map((exp) => (
                <tr key={exp.user_expense_id}>
                  <td>{exp.user_expense_id}</td>
                  <td>{exp.category}</td>
                  <td>{exp.description}</td>
                  <td>â‚¹{exp.amount}</td>
                  <td>{exp.only_date}</td>
                  <td>{exp.only_time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      <footer>
        <p>&copy; 2025 Expense Tracker</p>
        <img src={logo} alt="footer logo" />
      </footer>
    </div>
  );
};

export default Dashboard;
