import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  LineChart,
  Line,
  ResponsiveContainer,
} from "recharts";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#A28CFF", "#FF6699"];

const ExpenseCharts = ({ expenses }) => {
  if (!expenses || expenses.length === 0) {
    return <p>No expense data available to display charts.</p>;
  }

  // ðŸ§¾ Aggregate total amount by category for charts
  const categoryData = Object.values(
    expenses.reduce((acc, exp) => {
      if (!acc[exp.category]) {
        acc[exp.category] = { name: exp.category, amount: 0 };
      }
      acc[exp.category].amount += Number(exp.amount);
      return acc;
    }, {})
  );

  // ðŸ“… Prepare data for line/bar chart (by date)
  const dateData = Object.values(
    expenses.reduce((acc, exp) => {
      if (!acc[exp.only_date]) {
        acc[exp.only_date] = { date: exp.only_date, total: 0 };
      }
      acc[exp.only_date].total += Number(exp.amount);
      return acc;
    }, {})
  );

  return (
  <div
    style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "flex-start",
      flexWrap: "wrap",
      gap: "40px",
      width: "100%",
      maxWidth: "1100px",
      marginTop:'-55px'
    }}
  >
    <div style={{ flex: "1 1 300px", height: "300px", minWidth: "300px" }}>
      <h3 style={{ textAlign: "center" }}>Expense by Category</h3>
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie data={categoryData} dataKey="amount" nameKey="name" outerRadius={100} label>
            {categoryData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>

    <div style={{ flex: "1 1 300px", height: "300px", minWidth: "300px" }}>
      <h3 style={{ textAlign: "center" }}>Expense by Date</h3>
      <ResponsiveContainer width="100%" height="100%">
        <BarChart data={dateData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="total" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer>
    </div>

    <div style={{ flex: "1 1 300px", height: "300px", minWidth: "300px" }}>
      <h3 style={{ textAlign: "center" }}>Daily Expense Trend</h3>
      <ResponsiveContainer width="100%" height="100%">
        <LineChart data={dateData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="total" stroke="#8884d8" strokeWidth={2} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  </div>
);
};

export default ExpenseCharts;
