const ExpenseForm = ({ formData, setFormData }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "type") setFormData({ ...formData, type: value, category: "" });
    else setFormData({ ...formData, [name]: value });
  };

  const incomeCategories = ["Salary", "Freelance", "Business", "Investments", "Other Income"];
  const expenseCategories = ["Food", "Groceries", "Transport", "Rent", "Utilities", "Shopping", "Other"];

  const categories = formData.type === "Income" ? incomeCategories : formData.type === "Expense" ? expenseCategories : [];

  return (
    <div>
      <label>Type:</label>
      <select name="type" required value={formData.type} onChange={handleChange}>
        <option value="">Select Type</option>
        <option value="Income">Income</option>
        <option value="Expense">Expense</option>
      </select>

      <label>Category:</label>
      <select name="category" required value={formData.category} onChange={handleChange} disabled={!formData.type}>
        <option value="">Select Category</option>
        {categories.map((cat, idx) => <option key={idx} value={cat}>{cat}</option>)}
      </select>

      <label>Amount:</label>
      <input type="number" name="amount" placeholder="Enter amount" required value={formData.amount} onChange={handleChange} />
    </div>
  );
};

export default ExpenseForm;
