import { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import Cookies from "js-cookie";
import jwtDecode from "jwt-decode";
import "../styles/pagesstyle.css";

const Dashboard = () => {
  const [data, setData] = useState({ username: "", expenses: [], totalIncome: 0, totalExpense: 0, totalBalance: 0 });
  const [form, setForm] = useState({ amount: "", description: "", category: "Expense" });
  const navigate = useNavigate();

  const fetchDashboard = async () => {
    try {
      const res = await axios.get("http://localhost:5000/dashboard", {
        headers: { Authorization: `Bearer ${Cookies.get("jwt_token")}` },
      });
      setData(res.data);
    } catch (err) {
      console.error("Failed to fetch dashboard", err);
      navigate("/login");
    }
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/expenses", form, {
        headers: { Authorization: `Bearer ${Cookies.get("jwt_token")}` },
      });
      setForm({ amount: "", description: "", category: "Expense" });
      fetchDashboard();
    } catch (err) {
      alert("Error adding expense");
    }
  };

  const handleDeleteExpense = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/expenses/${id}`, {
        headers: { Authorization: `Bearer ${Cookies.get("jwt_token")}` },
      });
      fetchDashboard();
    } catch (err) {
      alert("Error deleting expense");
    }
  };

  useEffect(() => {
    const token = Cookies.get("jwt_token");
    if (!token) {
      navigate("/login");
      return;
    }
    fetchDashboard();
  }, [navigate]);

  const email = Cookies.get("jwt_token") ? jwtDecode(Cookies.get("jwt_token")).email : "";

  return (
    <div className="dashboard-container">
      <h2>Welcome {data.username} ({email})</h2>
      <Link to="/logout" className="logout-btn">Logout</Link>

      <div className="totals">
        <p>Total Income: â‚¹{data.totalIncome}</p>
        <p>Total Expense: â‚¹{data.totalExpense}</p>
        <p>Total Balance: â‚¹{data.totalBalance}</p>
      </div>

      <form onSubmit={handleAddExpense} className="expense-form">
        <input type="number" name="amount" placeholder="Amount" value={form.amount} onChange={(e) => setForm({ ...form, amount: e.target.value })} required />
        <input type="text" name="description" placeholder="Description" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} required />
        <select name="category" value={form.category} onChange={(e) => setForm({ ...form, category: e.target.value })} required>
          <option value="Expense">Expense</option>
          <option value="Income">Income</option>
        </select>
        <button type="submit">Add Expense</button>
      </form>

      <ul className="expense-list">
        {data.expenses.map((exp) => (
          <li key={exp.user_expense_id}>
            â‚¹{exp.amount} â€” {exp.description} ({exp.category}) - {exp.only_date} {exp.only_time}
            <button onClick={() => handleDeleteExpense(exp.user_expense_id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
