import { useEffect, useState } from "react";
import { useNavigate, Navigate } from "react-router-dom";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import Cookies from "js-cookie";
import {jwtDecode} from "jwt-decode";
import "../styles/pagesstyle.css";
import navLogo from "../assets/dashboard_logo.png";
import logo from "../assets/Logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [formData, setFormData] = useState({ amount: "", category: "Expense", description: "" });
  const [deleteIds, setDeleteIds] = useState("");
  const [loading, setLoading] = useState(true);

  // Check JWT token
  const token = Cookies.get("jwt_token");
  if (!token) {
    return <Navigate to="/login" replace />;
  }

  const decoded = jwt_decode(token);
  const email = decoded?.email || "";

  // Fetch user data & expenses
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
        setUserData(res.data);
        setExpenses(res.data.expenses || []);
      } catch (err) {
        console.error(err);
        navigate("/login");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [email, navigate]);

  // Handle form input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add expense
  const handleAddExpense = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/add-expense", { ...formData, email });
      alert("Expense added successfully!");
      setFormData({ amount: "", category: "Expense", description: "" });
      // Refresh data
      const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
      setUserData(res.data);
      setExpenses(res.data.expenses || []);
    } catch (err) {
      console.error(err);
      alert("Failed to add expense.");
    }
  };

  // Delete expense
  const handleDeleteExpense = async (e) => {
    e.preventDefault();
    const idsArray = deleteIds
      .split(",")
      .map((id) => id.trim())
      .filter((id) => id !== "");

    if (!idsArray.length) {
      alert("Enter at least one valid expense ID.");
      return;
    }

    try {
      await axios.delete("http://localhost:5000/delete-expense", {
        data: { email, ids: idsArray },
        headers: { "Content-Type": "application/json" },
      });
      alert("Expense(s) deleted successfully!");
      setDeleteIds("");
      const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
      setUserData(res.data);
      setExpenses(res.data.expenses || []);
    } catch (err) {
      console.error(err);
      alert("Failed to delete expenses.");
    }
  };

  // Download PDF
  const handleDownloadPDF = () => {
    if (!expenses.length) {
      alert("No data available to generate PDF.");
      return;
    }
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Expense Report", 14, 15);
    const headers = [["ID", "Category", "Description", "Amount"]];
    const data = expenses.map((item, index) => [
      index + 1,
      item.category || "Unknown",
      item.description || "No description",
      item.amount,
    ]);
    doc.autoTable({ startY: 20, head: headers, body: data, theme: "grid" });
    doc.save("expenses.pdf");
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard-content">
      <div className="dashboard-nav-container">
        <nav className="dashboard-navbar">
          <div className="dashboard-logo">
            <img src={navLogo} alt="Logo" className="logo" />
          </div>
        </nav>
      </div>

      <section>
        <h1>Welcome, {userData.username}</h1>
        <div>
          <p>Total Income: ₹{userData.totalIncome}</p>
          <p>Total Expense: ₹{userData.totalExpense}</p>
          <p>Total Balance: ₹{userData.totalBalance}</p>
          <button onClick={handleDownloadPDF}>Download PDF</button>
        </div>
      </section>

      <section>
        <h2>Add Expense</h2>
        <form onSubmit={handleAddExpense}>
          <input
            type="number"
            name="amount"
            placeholder="Amount"
            value={formData.amount}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />
          <select name="category" value={formData.category} onChange={handleChange} required>
            <option value="Expense">Expense</option>
            <option value="Income">Income</option>
          </select>
          <button type="submit">Add Expense</button>
        </form>
      </section>

      <section>
        <h2>Delete Expense</h2>
        <form onSubmit={handleDeleteExpense}>
          <input
            type="text"
            placeholder="Enter IDs e.g., 1,2,3"
            value={deleteIds}
            onChange={(e) => setDeleteIds(e.target.value)}
            required
          />
          <button type="submit">Delete Expense(s)</button>
        </form>
      </section>

      <section>
        <h2>Your Expenses</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Category</th>
              <th>Description</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {expenses.map((exp, idx) => (
              <tr key={exp.user_expense_id}>
                <td>{idx + 1}</td>
                <td>{exp.category}</td>
                <td>{exp.description}</td>
                <td>₹ {exp.amount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      <footer>
        <p>&copy; 2025 Expense Tracker</p>
        <img src={logo} alt="footer-logo" />
      </footer>
    </div>
  );
};

export default Dashboard;
