import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import { jwtDecode } from "jwt-decode";

const ExpenseForm = ({ onSuccess }) => {
  const [formData, setFormData] = useState({
    amount: "",
    category: "Expense",
    description: "",
    user_expense_id: "",
  });
  const [isUpdate, setIsUpdate] = useState(false);
  const [expenses, setExpenses] = useState([]);

  const token = Cookies.get("jwt_token");
  const email = token ? jwtDecode(token).email : null;

  // Fetch expenses for update selection
  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
        setExpenses(res.data.expenses || []);
      } catch (err) {
        console.error("Error fetching expenses:", err);
      }
    };
    if (email) fetchExpenses();
  }, [email]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSelectExpense = (e) => {
    const selectedId = e.target.value;
    if (selectedId) {
      const selectedExpense = expenses.find(exp => exp.user_expense_id == selectedId);
      if (selectedExpense) {
        setFormData({
          amount: selectedExpense.amount,
          category: selectedExpense.category,
          description: selectedExpense.description || "",
          user_expense_id: selectedExpense.user_expense_id,
        });
        setIsUpdate(true);
      }
    } else {
      setFormData({
        amount: "",
        category: "Expense",
        description: "",
        user_expense_id: "",
      });
      setIsUpdate(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isUpdate) {
        await axios.put("http://localhost:5000/update-expense", {
          email,
          user_expense_id: formData.user_expense_id,
          amount: formData.amount,
          category: formData.category,
          description: formData.description,
        });
        alert("Expense updated successfully!");
      } else {
        await axios.post("http://localhost:5000/add-expense", {
          email,
          amount: formData.amount,
          type: formData.category, // Assuming type is category
          category: formData.category,
          description: formData.description,
        });
        alert("Expense added successfully!");
      }
      setFormData({ amount: "", category: "Expense", description: "", user_expense_id: "" });
      setIsUpdate(false);
      onSuccess(); // Refresh data
    } catch (err) {
      console.error(err);
      alert(`Failed to ${isUpdate ? "update" : "add"} expense.`);
    }
  };

  return (
    <div className="expense-card">
      <p className="expense-card-title">{isUpdate ? "Update Expense" : "Add Expense"}</p>
      <div className="expense-card-from">
        <form onSubmit={handleSubmit}>
          {expenses.length > 0 && (
            <>
              <label htmlFor="user_expense_id">Select Expense to Update (leave blank for add):</label><br />
              <select name="user_expense_id" value={formData.user_expense_id} onChange={handleSelectExpense}>
                <option value="">Add New</option>
                {expenses.map((exp) => (
                  <option key={exp.user_expense_id} value={exp.user_expense_id}>
                    ID {exp.user_expense_id}: {exp.category} - ₹{exp.amount}
                  </option>
                ))}
              </select><br />
            </>
          )}
          <label htmlFor="amount">Enter the cost(amount): </label><br />
          <input
            type="number"
            name="amount"
            placeholder="Enter amount"
            required
            value={formData.amount}
            onChange={handleChange}
          />
          <label htmlFor="category">Category (income / expense): </label><br />
          <select name="category" required value={formData.category} onChange={handleChange}>
            <option value="">Select Category</option>
            <option value="Income">Income</option>
            <option value="Expense">Expense</option>
          </select><br />
          <label htmlFor="description">Enter the description(detail of data): </label><br />
          <input
            type="text"
            name="description"
            placeholder="Enter details"
            value={formData.description}
            onChange={handleChange}
          />
          <p><strong className="highlights">Note:</strong> Fill the details carefully</p>
          <div className="button-container button-add">
            <button type="submit">{isUpdate ? "Update Expense" : "Add Expense"}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ExpenseForm;
