import { Component } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom"; // v7 hooks
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "../styles/pagesstyle.css";
import navLogo from "../assets/dashboard_logo.png";
import homeLogo from "../assets/expence_tracker_home_page.png";
import logo from "../assets/Logo.png";
import ExpenseCharts from "./ExpenseCharts";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userData: null,
      error: "",
      expenses: [],
      loading: true,
      deleteIds: "",
      formData: {
        amount: "",
        category: "",
        description: "",
        email: ""
      }
    };
  }

  componentDidMount() {
    const { navigate } = this.props;
    this.checkAuthAndFetchData();
  }

  checkAuthAndFetchData = async () => {
    const { navigate } = this.props;
    try {
      const res = await axios.get("http://localhost:5000/me");
      const email = res.data.email;
      this.setState({ formData: { ...this.state.formData, email } });
      this.fetchUserData(email);
      this.fetchExpenses(email);
    } catch (err) {
      navigate("/login"); // Redirect if not authenticated
    }
  };

  fetchUserData = (email) => {
    axios
      .get(`http://localhost:5000/dashboard?email=${email}`)
      .then((res) => this.setState({ userData: res.data }))
      .catch(() => this.setState({ error: "Failed to load data." }));
  };

  fetchExpenses = (email) => {
    axios
      .get(`http://localhost:5000/get-pdf?email=${email}`)
      .then((res) => {
        console.log("Fetched expenses:", res.data);
        this.setState({ expenses: res.data });
      })
      .catch((err) => {
        console.error("Error fetching expenses:", err);
        this.setState({ error: "Failed to load expenses." });
      })
      .finally(() => this.setState({ loading: false }));
  };

  handleChange = (e) => {
    const { formData } = this.state;
    this.setState({
      formData: { ...formData, [e.target.name]: e.target.value }
    });
  };

  handleAddExpense = async (e) => {
    e.preventDefault();
    const { formData } = this.state;
    const email = formData.email;
    try {
      const response = await axios.post("http://localhost:5000/add-expense", {
        ...formData,
        email
      });
      alert(response.data.message || "Expense added successfully!");
      this.setState({
        formData: { amount: "", category: "", description: "", email }
      });
      this.fetchUserData(email);
      this.fetchExpenses(email);
    } catch (error) {
      console.error("Error adding expense:", error);
      alert("Failed to add expense. Try again!");
    }
  };

  handleDeleteExpense = async (e) => {
    e.preventDefault();
    const { formData, deleteIds } = this.state;
    const email = formData.email;

    const idsArray = deleteIds
      .split(",")
      .map((id) => id.trim())
      .filter((id) => id !== "");

    if (idsArray.length === 0) {
      alert("Please enter at least one valid expense ID.");
      return;
    }

    try {
      const response = await axios.delete("http://localhost:5000/delete-expense", {
        data: { email, ids: idsArray },
        headers: { "Content-Type": "application/json" }
      });

      alert(response.data.message);
      this.setState({ deleteIds: "" });
      this.fetchUserData(email);
      this.fetchExpenses(email);
    } catch (error) {
      console.error("Error deleting expense:", error.response?.data || error.message);
      alert("Failed to delete expenses. Try again!");
    }
  };

  handleDownloadPDF = async () => {
    const { formData } = this.state;
    const email = formData.email;
    try {
      const res = await axios.get(`http://localhost:5000/get-pdf?email=${email}`);
      const expenses = res.data;
      console.log("Fetched data for PDF:", expenses);

      if (!expenses.length) {
        alert("No data available to generate PDF.");
        return;
      }

      const doc = new jsPDF();
      doc.setFontSize(16);
      doc.text("Expense Report", 14, 15);

      const headers = [["ID", "Category", "Description", "Amount", "Date", "Time"]];
      const data = expenses.map((item, index) => [
        index + 1,
        item.category || "Unknown",
        item.description || "No description",
        item.amount,
        item.only_date || "N/A",
        item.only_time || "N/A"
      ]);

      doc.autoTable({
        startY: 20,
        head: headers,
        body: data,
        theme: "grid",
        styles: { halign: "center" },
        headStyles: { fillColor: [0, 153, 255] }
      });

      doc.save("expenses.pdf");
    } catch (error) {
      console.error("Error generating PDF:", error);
      this.setState({ error: "Failed to generate PDF." });
    }
  };

  render() {
    const { userData, error, deleteIds, formData, loading, expenses } = this.state;

    if (!userData) return <p>Loading...</p>;

    return (
      <div className="dashboard-content">
        <div className="dashboard-nav-container">
          <nav className="dashboard-navbar">
            <div className="dashboard-logo">
              <img src={navLogo} alt="Logo" className="logo" />
            </div>
            <div className="dashboard-buttons">
              <button className="dashboard-button"><a href="#sectionHome">Home</a></button>
              <button className="dashboard-button"><a href="#sectionExpense">Expenses</a></button>
              <button className="dashboard-button"><a href="#sectionData">Your Data</a></button>
            </div>
          </nav>
        </div>
        <section id="sectionHome">
          <div className="home-content">
            <div className="home-container">
              <div className="home-card">
                <div className="home-card-description">
                  <h1 className="home-title">Hi, <span className="highlights">{userData.username}</span></h1>
                  <h2 className="home-title">Welcome to <span className="highlights">Expense Tracker</span> Dashboard</h2>
                  <h3 className="home-title">Track your Expenses Easily</h3>
                  <p className="home-description">Manage your daily expenses efficiently and take control of your financial health.</p>
                  <ul className="home-description">
                    <li>➕ Add your daily expenses</li>
                    <li>📈 View spending insights</li>
                    <li>💰 Set monthly budgets</li>
                    <li>🔔 Get expense alerts</li>
                  </ul>
                </div>
                <div className="home-card-image">
                  <img src={homeLogo} alt="Logo" className="home-image" />
                </div>
              </div>
            </div>
          </div>
        </section>

        <section id="sectionExpense">
          <div className="expense-content">
            <div className="expense-cards">
              <div className="expense-card">
                <p className="expense-card-title">Add Expense</p>
                <div className="expense-card-from">
                  <form onSubmit={this.handleAddExpense}>
                    <label htmlFor="amount">Enter the cost(amount): </label><br />
                    <input type="number" name="amount" placeholder="Enter amount" required value={formData.amount} onChange={this.handleChange} />
                    <label htmlFor="select">Category(income / expense): </label><br />
                    <select name="category" required value={formData.category} onChange={this.handleChange}>
                      <option value="">Select Category</option>
                      <option value="Income">Income</option>
                      <option value="Expense">Expense</option>
                    </select><br />
                    <label htmlFor="description">Enter the description(detail of data): </label><br />
                    <input type="text" name="description" placeholder="Enter details" value={formData.description} onChange={this.handleChange} />
                    <p><strong className="highlights">Note:</strong> Once you add the data you can't change it, So fill the details carefully</p>
                    <div className="button-container button-add">
                      <button type="submit">Add Expense</button>
                    </div>
                  </form>
                </div>
              </div>

              <div className="expense-card">
                <p className="expense-card-title">Delete Expense</p>
                <div className="expense-card-from">
                  <form onSubmit={this.handleDeleteExpense}>
                    <label htmlFor="id">Enter Expense ID(s):</label><br />
                    <input
                      type="text"
                      name="id"
                      placeholder="Enter Expense ID(s) e.g., 1,2,3"
                      required
                      value={deleteIds}
                      onChange={(e) => this.setState({ deleteIds: e.target.value })}
                    />
                    <p>You can see your data in the table below and enter IDs for the records you want to delete.</p>
                    <p><strong className="highlights">Note:</strong> Once you delete the data, it cannot be recovered.</p>
                    <div className="button-container button-delete">
                      <button type="submit">Delete Expenses</button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </section>

        <section id="sectionData">
          <div className="data-content">
            <div className="data-container">
              <div className="data-cards">
                <div className="data-top-cards">
                  <div className="data-top-card">
                    <h3>Total Income: ₹<b className="highlights">{userData.totalIncome}</b></h3>
                  </div>
                  <div className="data-top-card">
                    <h3>Total Expense: ₹<b className="highlights">{userData.totalExpense}</b></h3>
                  </div>
                  <div className="data-top-card">
                    <h3>Total Balance: ₹<b className="highlights">{userData.totalBalance}</b></h3>
                  </div>
                  <div className="data-top-card">
                    <button onClick={this.handleDownloadPDF} disabled={loading}>Download PDF 📄</button>
                  </div>
                </div>
                <div className="data-bottom-card">
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Category</th>
                        <th>Description</th>
                        <th>Amount</th>
                        <th>Date</th>
                        <th>Time</th>
                      </tr>
                    </thead>
                    <tbody>
                      {userData.expenses.map((exp) => (
                        <tr key={exp.id}>
                          <td>{exp.user_expense_id}</td>
                          <td>{exp.category}</td>
                          <td>{exp.description}</td>
                          <td>₹ {exp.amount}</td>
                          <td>{exp.only_date}</td>
                          <td>{exp.only_time}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                 
              </div>
            </div>
          </div>
        </section>

        <section id="sectionGraph">
          <div className="data-content">
            <div className="data-container">
              <div className="data-cards">
                <div className="data-card">
                  <ExpenseCharts expenses={expenses} />
                </div>
              </div>
            </div>
          </div>
        </section>

        <div className="footer">
          <p>&copy; 2025 Expense Tracker </p> <img src={logo} alt="footor-logo" />
        </div>
      </div>
    );
  }
}

function withRouter(Component) {
  return function (props) {
    const navigate = useNavigate();
    const params = useParams();
    const location = useLocation();
    return <Component {...props} navigate={navigate} params={params} location={location} />;
  };
}

export default withRouter(Dashboard);
