import { useState, useEffect, useCallback } from "react";
import { useNavigate} from "react-router-dom";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import Cookies from "js-cookie";
import { jwtDecode } from "jwt-decode";
import "../styles/pagesstyle.css";
import navLogo from "../assets/dashboard_logo.png";
import homeLogo from "../assets/expence_tracker_home_page.png";
import logo from "../assets/Logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [formData, setFormData] = useState({ amount: "", category: "Expense", description: "" });
  const [deleteIds, setDeleteIds] = useState([]);
  const [expenses, setExpenses] = useState([]);

  const token = Cookies.get("jwt_token");

  // Decode token to get email
  const email = token ? jwtDecode(token).email : null;

  // Fetch user data
  const fetchUserData = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/dashboard?email=${email}`);
      setUserData(res.data);
      setExpenses(res.data.expenses || []);
    } catch (err) {
      console.error("Error fetching user data:", err);
      navigate("/login");
    } finally {
      setLoading(false);
    }
  }, [email, navigate]);

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }
    fetchUserData();
  }, [token, fetchUserData, navigate]);

  if (!token) {
    return null; // Optional fallback
  }

  if (loading || !userData) {
    return <p>Loading...</p>;
  }

  // Handle form input
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add expense
  const handleAddExpense = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/add-expense", { ...formData, email });
      alert("Expense added successfully!");
      setFormData({ amount: "", category: "Expense", description: "" });
      fetchUserData();
    } catch (err) {
      console.error(err);
      alert("Failed to add expense.");
    }
  };

  // Delete expense
  const handleDeleteExpense = async (e) => {
    e.preventDefault();
    const idsArray = deleteIds
      .toString()
      .split(",")
      .map((id) => id.trim())
      .filter((id) => id !== "");
    if (idsArray.length === 0) {
      alert("Please enter valid IDs to delete.");
      return;
    }
    try {
      await axios.delete("http://localhost:5000/delete-expense", {
        data: { email, ids: idsArray },
        headers: { "Content-Type": "application/json" },
      });
      alert("Expenses deleted successfully!");
      setDeleteIds([]);
      fetchUserData();
    } catch (err) {
      console.error(err);
      alert("Failed to delete expenses.");
    }
  };

  // Generate PDF
  const handleDownloadPDF = () => {
    if (!expenses.length) {
      alert("No expenses to generate PDF.");
      return;
    }

    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Expense Report", 14, 15);

    const headers = [["ID", "Category", "Description", "Amount"]];
    const data = expenses.map((exp, index) => [
      index + 1,
      exp.category,
      exp.description,
      exp.amount,
    ]);

    doc.autoTable({
      startY: 20,
      head: headers,
      body: data,
      theme: "grid",
      styles: { halign: "center" },
      headStyles: { fillColor: [0, 153, 255] },
    });

    doc.save("expenses.pdf");
  };

  return (
    <div className="dashboard-content">
      {/* Navbar */}
      <div className="dashboard-nav-container">
        <nav className="dashboard-navbar">
          <div className="dashboard-logo">
            <img src={navLogo} alt="Logo" className="logo" />
          </div>
          <div className="dashboard-buttons">
            <button className="dashboard-button"><a href="#sectionHome">Home</a></button>
            <button className="dashboard-button"><a href="#sectionExpense">Expenses</a></button>
            <button className="dashboard-button"><a href="#sectionData">Your Data</a></button>
          </div>
        </nav>
      </div>

      {/* Home Section */}
      <section id="sectionHome">
        <div className="home-content">
          <div className="home-container">
            <div className="home-card">
              <div className="home-card-description">
                <h1>Hi, <span className="highlights">{userData.username}</span></h1>
                <h2>Welcome to <span className="highlights">Expense Tracker</span></h2>
                <p>Manage your daily expenses efficiently and take control of your financial health.</p>
              </div>
              <div className="home-card-image">
                <img src={homeLogo} alt="Home" />
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Expense Section */}
      <section id="sectionExpense">
        <div className="expense-content">
          {/* Add Expense */}
          <div className="expense-card">
            <p className="expense-card-title">Add Expense</p>
            <form onSubmit={handleAddExpense}>
              <input type="number" name="amount" placeholder="Amount" value={formData.amount} onChange={handleChange} required />
              <select name="category" value={formData.category} onChange={handleChange} required>
                <option value="Income">Income</option>
                <option value="Expense">Expense</option>
              </select>
              <input type="text" name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
              <button type="submit">Add Expense</button>
            </form>
          </div>

          {/* Delete Expense */}
          <div className="expense-card">
            <p className="expense-card-title">Delete Expense</p>
            <form onSubmit={handleDeleteExpense}>
              <input
                type="text"
                placeholder="Enter IDs e.g., 1,2,3"
                value={deleteIds}
                onChange={(e) => setDeleteIds(e.target.value)}
                required
              />
              <button type="submit">Delete</button>
            </form>
          </div>
        </div>
      </section>

      {/* Data Section */}
      <section id="sectionData">
        <div className="data-content">
          <div className="data-container">
            <h3>Total Income: ₹{userData.totalIncome}</h3>
            <h3>Total Expense: ₹{userData.totalExpense}</h3>
            <h3>Total Balance: ₹{userData.totalBalance}</h3>
            <button onClick={handleDownloadPDF}>Download PDF</button>

            <table className="data-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Category</th>
                  <th>Description</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {expenses.map((exp, idx) => (
                  <tr key={idx}>
                    <td>{exp.user_expense_id}</td>
                    <td>{exp.category}</td>
                    <td>{exp.description}</td>
                    <td>₹ {exp.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </section>

      <footer>
        <p>&copy; 2025 Expense Tracker</p>
        <img src={logo} alt="footer-logo" />
      </footer>
    </div>
  );
};

export default Dashboard;
